angular.module("schemaForm").run(["$templateCache", function($templateCache) {$templateCache.put("directives/decorators/bootstrap/tinymce/tinymce.html","<div class=\"form-group\" ng-class=\"{\'has-error\': hasError()}\"><label class=\"control-label\" ng-show=\"showTitle()\">{{form.title}}</label> <textarea tx-tinymce=\"form.tinymceOptions\" ng-model=\"$$value$$\" style=\"background-color: white\" sf-field-model=\"\" sf-changed=\"form\" schema-validate=\"form\"></textarea> <span class=\"help-block\">{{ (hasError() && errorMessage(schemaError())) || form.description}}</span></div>");}]);
angular.module('schemaForm-tinymce', ['schemaForm', 'tx-tinymce']).config(
['schemaFormProvider', 'schemaFormDecoratorsProvider', 'sfPathProvider','sfBuilderProvider',
  function(schemaFormProvider,  schemaFormDecoratorsProvider, sfPathProvider, sfBuilderProvider) {

    var wysiwyg = function(name, schema, options) {
    if (schema.type === 'string' && schema.format == 'html') {
      var f = schemaFormProvider.stdFormObj(name, schema, options);
      f.key  = options.path;
      f.type = 'wysiwyg';
      options.lookup[sfPathProvider.stringify(options.path)] = f;
      return f;
    }
  };

    schemaFormProvider.defaults.string.unshift(wysiwyg);

  //Add to the bootstrap directive
    schemaFormDecoratorsProvider.addMapping('bootstrapDecorator', 'wysiwyg',
    'directives/decorators/bootstrap/tinymce/tinymce.html');
    schemaFormDecoratorsProvider.createDirective('wysiwyg',
    'directives/decorators/bootstrap/tinymce/tinymce.html');
    var sfField = sfBuilderProvider.builders.sfField;
    var sfConditional = sfBuilderProvider.builders.condition;
    var ngModel = sfBuilderProvider.builders.ngModel;
    var ngModelOptions = sfBuilderProvider.builders.ngModelOptions;
    var defaults = [sfField, ngModel, sfConditional];
    schemaFormDecoratorsProvider.defineAddOn('bootstrapDecorator', 'wysiwyg', 'directives/decorators/bootstrap/tinymce/tinymce.html', defaults);


  }]);
